import React from "react";

const Book = (props) => {
  const { img, title, author, id } = props;
  // console.log(props);
  const clickHandler = () => {
    alert("hello world");
  };
  const complexExample = (aniket) => {
    console.log(aniket);
  };
  return (
    <article className="bookarticle">
      <img src={img} alt="" />
      <h1>{title}</h1>
      <h4>{author}</h4>
      <button type="button" onClick={clickHandler}>
        click handler
      </button>
      <button type="button" onClick={() => complexExample(author)}>
        click me
      </button>
      {/* <p>{2 + 2}</p> */}
    </article>
  );
};

export default Book;
