import React from "react";
import ReactDom from "react-dom";

// ***** adding css to react book project ******

import "./index.css";

// **** JSX rules and regulation***
/*
- return single element
- use camelCase for html attribute(like onclick becomes onClick)
- className instead of class
- close every element even img and input
- keep an eye on formating

*/

function HolaGreetings() {
  return (
    <React.Fragment>
      <div className="maindiv">
        <h1>hello world inside a div</h1>
        <img src="" alt="" />
        <input type="text" />
      </div>
    </React.Fragment>
  );
}
//****/ jsx can be returned like this also***

// const Greetings = () => {
//   return React.createElement("h1", {}, "hello world");
// };

// ReactDom.render(<HolaGreetings />, document.getElementById("root"));

// *** nested components, react tools ******

function GreetingsByNested() {
  return (
    <div>
      <Person />
      <Message />
    </div>
  );
}

const Person = () => <h2>jhon doe</h2>;

const Message = () => {
  return <p>this is a message</p>;
};

// ReactDom.render(<GreetingsByNested />, document.getElementById("root"));

// ***** making a book list ******

// setup variable
const books = [
  {
    id: 1,
    author: "Wonder House Book",
    title: "Boxset of 10 Board Books for Kids Board book",
    img: "https://m.media-amazon.com/images/I/711c-uf6AFL._AC_UY327_FMwebp_QL65_.jpg",
  },
  {
    id: 2,
    author: "Antoine de Saint-Exupéry",
    img: "https://images-eu.ssl-images-amazon.com/images/I/512ylJKUoDL._SX198_BO1,204,203,200_QL40_FMwebp_.jpg",
    title: "Best of Children’s Classics",
  },
  {
    id: 3,
    author: "Joseph Murphy",
    img: "https://m.media-amazon.com/images/I/71UwSHSZRnS._AC_UY327_FMwebp_QL65_.jpg",
    title: "The Power of your subconscious mind",
  },
];

function BookList() {
  return (
    <section className="booklist">
      {books.map((book) => {
        return <Book key={book.id} {...book}></Book>;
      })}
    </section>
  );
}

const Book = (props) => {
  const { img, title, author, id } = props;
  console.log(props);
  const clickHandler = () => {
    alert("hello world");
  };
  return (
    <article className="bookarticle">
      <img src={img} alt="" />
      <h1>{title}</h1>
      <h4>{author}</h4>
      <button type="button" onClick={clickHandler}>
        click me
      </button>
      {/* <p>{2 + 2}</p> */}
    </article>
  );
};

ReactDom.render(<BookList />, document.getElementById("root"));
