import React from "react";
import ReactDom from "react-dom";

// ***** adding css to react book project ******

import "./index.css";
import { books } from "./books.js";
import Book from "./BookFunction.js";

// ***** making a book list ******

function BookList() {
  return (
    <section className="booklist">
      {books.map((book) => {
        return <Book key={book.id} {...book}></Book>;
      })}
    </section>
  );
}

ReactDom.render(<BookList />, document.getElementById("root"));

// **** JSX rules and regulation***
/*
- return single element
- use camelCase for html attribute(like onclick becomes onClick)
- className instead of class
- close every element even img and input
- keep an eye on formating

*/
