import React from "react";
import ReactDom from "react-dom";

// ***** adding css to react book project ******

import "./index.css";

// **** JSX rules and regulation***
/*
- return single element
- use camelCase for html attribute(like onclick becomes onClick)
- className instead of class
- close every element even img and input
- keep an eye on formating

*/

function HolaGreetings() {
  return (
    <React.Fragment>
      <div className="maindiv">
        <h1>hello world inside a div</h1>
        <img src="" alt="" />
        <input type="text" />
      </div>
    </React.Fragment>
  );
}
//****/ jsx can be returned like this also***

// const Greetings = () => {
//   return React.createElement("h1", {}, "hello world");
// };

// ReactDom.render(<HolaGreetings />, document.getElementById("root"));

// *** nested components, react tools ******

function GreetingsByNested() {
  return (
    <div>
      <Person />
      <Message />
    </div>
  );
}

const Person = () => <h2>jhon doe</h2>;

const Message = () => {
  return <p>this is a message</p>;
};

// ReactDom.render(<GreetingsByNested />, document.getElementById("root"));

// ***** making a book list ******

function BookList() {
  return (
    <section className="booklist">
      <Book job="developer" />
      <Book title="random title" number={22} />
    </section>
  );
}
// setup variable
const firstBook = {
  aurthor: "Wonder House Book",
  title: "Boxset of 10 Board Books for Kids Board book",
  img: "https://m.media-amazon.com/images/I/711c-uf6AFL._AC_UY327_FMwebp_QL65_.jpg",
};

const Book = (props) => {
  console.log(props);
  return (
    <article className="bookarticle">
      <img src={props.img} alt="" />
      <h1>{props.title}</h1>
      <h4>{props.aurthor.toLocaleUpperCase()}</h4>
      {/* <p>{2 + 2}</p> */}
      <p>{props.job}</p>
    </article>
  );
};

const Image = () => {
  return (
    <img
      src="https://m.media-amazon.com/images/I/711c-uf6AFL._AC_UY327_FMwebp_QL65_.jpg"
      alt=""
    />
  );
};
const Title = () => {
  return <h1>Boxset of 10 Board Books for Kids Board book</h1>;
};
const Aurthor = () => (
  <h4 style={{ color: "#617d98", fontSize: "0.75rem", marginTop: "0.25rem" }}>
    Wonder House Book
  </h4>
);
ReactDom.render(<BookList />, document.getElementById("root"));
